package com.kh.day02.loop.exercise;

public class Examcise_ForDouble1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//구구단
			for(int k = 2; k<10; k++) {
				System.out.println(k +"단");				
			for(int h = 1; h <10; h++) {
				System.out.print(k + " * " + h+ " = " + k*h);
			}
		}
	}
}
