package com.kh.day02.loop.exercise;

public class Examcise_ForDouble3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//구구단
		for(int k =2; k<10; k++) {
			for(int h = 2; h<10; h++) {
				System.out.println(h + "*" +k+"="+h+"\t");
			}
			System.out.println();
		}
	}

}








